var part1Tests = []struct {
	in  string
	out int
}{
	{"(())", 0},
	{"()()", 0},
	{"(((", 3},
	{"(()(()(", 3},
	{"))(((((", 3},
	{"())", -1},
	{"))(", -1},
	{")))", -3},
	{")())())", -3},
	{input, 232},
}

func TestPart1(t *testing.T) {
	for _, tt := range part1Tests {
		tt := tt

		t.Run(tt.in, func(t *testing.T) {
			t.Parallel()

			out, err := day01.Part1(tt.in)
			if err != nil {
				t.Error(err)
			}

			assert.Equal(t, tt.out, out)
		})
	}
}
